import React, { useState } from "react";
import {
  Alert,
  Modal,
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Button,
  FlatList,
  Image,
} from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";
import connect from "../api/connect";

const News = () => {
  const [tabs, setTabs] = useState();

  const request = connect.get("/Release", {}).then((result) => {
    setTabs(result.data);
  });

  return (
    <View style={styles.container}>
      {/* //TabBars */}
      <View style={styles.tabbars}>
        <FlatList
          contentContainerStyle={{flexDirection: 'row', justifyContent: 'space-around',width: '100%' }}
          data={tabs}
          renderItem={({ item }) => {
            return (
              <TouchableOpacity style={styles.buttonTab}>
                <Text>{item.name}</Text>
              </TouchableOpacity>
            );
          }}
        />
      </View>
      <View style={styles.contentContainer}>
          <Text>alo</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    // flex: 1,
    backgroundColor: "white",
  },
  tabbars:{
    marginTop: 50,
    flexDirection: 'column',
  },
  buttonTab:{
    padding: 10,
    backgroundColor: 'pink',
    borderRadius: 5,
    width: 100,
    alignItems: 'center',
  },
  contentContainer:{
    // backgroundColor: 'pink',
    // width: "90%",
    // height: 500,
  }
});

export default News;
