          >
            <Image
              source={selectedAllBrands ? icons.exit : icons.dropdown}
              style={{ height: 20, width: 20 }}
            />
          </TouchableOpacity>
        </TouchableOpacity>
        <SmallModalAllBrands
          isOpen={smallModalAllBrands}
          onClose={handleCloseAllBrands}
          onPressAdd={() => setSmallModalAllBrands(false)}
        >
          <FlatList
            data={tabView}
            renderItem={({ item, index }) => (
              <TouchableOpacity
                onPress={() => {
                  setSmallModalAllBrands(false);
                  setSelectedAllBrands(item);
                }}
              >
                <Text style={{ fontWeight: "500", marginVertical: 7 }}>
                  {item.name}
                </Text>
              </TouchableOpacity>
