          onPress={() => setSmallModalDate(true)}
        >
          <Text>{selectedDate != null ? selectedDate.data : "Date"}</Text>
          <TouchableOpacity
            onPress={selectedDate ? handleCancelSelect : handleSelectDate}
          >
            <Image
              source={selectedDate ? icons.exit : icons.dropdown}
              style={{ height: 20, width: 20 }}
            />
          </TouchableOpacity>
        </TouchableOpacity>
        <SmallModalDate
          isOpen={smallModalDate}
          onClose={handleCloseDate}
          onPressAdd={() => setSmallModalDate(false)}
        >
          <FlatList
